import React, { Component } from 'react';
import { StyleSheet, Text, View, TextInput, Button, FlatList, TouchableOpacity } from 'react-native';
import io from 'socket.io-client'
import Automerge from 'automerge';
import Connection from './connection'

const app = require('express')();
const http = require('http').Server(app);
const io2 = require('socket.io')(http);

io.on('connection', (socket) => {
  console.log('a user is connected');

  const connection = new Automerge.Connection(docSet, socket)

  socket.on('data', ({input, name}) =>Â {
      let doc = docSet.getDoc(name)
      if (doc) {
          doc = Automerge.change(doc, doc => {
              doc.chards = ['hei']
          })
          docSet.setDoc(name, doc)           

          if(!(input instanceof Buffer)) {
              input = Buffer.from(input, 'utf8')
          }

          connection.receiveData(input)
      }

  })

  socket.on('add doc', (data) => {
      const initDoc = Automerge.change(Automerge.init(), doc => {doc.title = data, doc.cards = []})
      docSet.setDoc(data, initDoc)
  })

  socket.on('delete doc', (data) => {
      let doc = docSet.getDoc(data)
      if (doc) {
          docSet.removeDoc(data)
          console.log('doc deleted ' + data);
      }
  })

  socket.on('disconnect', () => {
      console.log('user disconnected');
  });
});


http.listen(3000, () => {
  console.log('listening on *:3000');
});


const docSet = new Automerge.DocSet()

docSet.registerHandler((docId, doc) => {
  console.log(`[${docId}] ${JSON.stringify(doc)}`);
})




const Item = ({ item, onPress, style }) => {
  <TouchableOpacity onPress={onPress} style={[styles.item, style]}>
    <Text style={styles.title}>{item[1].name}</Text>
  </TouchableOpacity>

}


export default class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      message: '',
      docname: '',
      docarray: []
    }
  }


  componentDidMount() {


    this.socket = io("http://129.242.19.137:3000");
    this.connection = new Automerge.Connection(docSet, this.socket)

    this.socket.on('data', ({data, name}) => {
      let doc = docSet.getDoc(name)
      doc = Automerge.change(doc, doc => {
          doc.chards = ['hei']
      })
      docSet.setDoc(name, doc)
      this.connection.receiveData(data)
    })

    this.socket.on('close', () => {
      console.log(`[${HOST}:${PORT}] connection closed`)
    })

    this.socket.on('error', (err) => {
      console.log(`[${socket.remoteAddress}:${socket.remotePort}] error: ${err}`)
    })
  }

  submitMessage() {
    let doc = docSet.getDoc(this.state.docname)
    doc = Automerge.change(doc, doc => {
        doc.chards = ['hei']
    })
    docSet.setDoc(this.state.docname, doc)


    // this.socket.emit("data", {input: this.state.message, name: this.state.docname})
    this.setState({message: ''})
  }

  addDoc() {
    console.log('doc name: ' + this.state.docname);
    let initDoc = Automerge.change(Automerge.init(), doc => {doc.title = this.state.docname, doc.cards = []})
    docSet.setDoc(this.state.docname, initDoc)

    this.state.docarray = Array.from(docSet.docs)
    // console.log(docSet);

    console.log(this.state.docarray);
  }




  renderItem({ item }) {
    const backgroundColor = "#f9c2ff";
    // console.log(item);
    return (
      <Item
      item={item}
      // onPress={() => openDoc({navigation}, item) }
      style={{ backgroundColor }}
      />
      );
    }



  render() {
    return (
      <View style={styles.container}>
        <View style={{flex: 1}}>
           <Text style={{fontSize: 30}}>Toohak!</Text>
           <View style={{marginVertical: 8, borderBottomColor: '#737373', borderBottomWidth: StyleSheet.hairlineWidth}} />
        </View>
        <View style={{marginVertical: 8, borderBottomColor: '#737373', borderBottomWidth: StyleSheet.hairlineWidth}} />

        <View >
          <FlatList
            data={this.state.docarray}
            renderItem={this.renderItem}
            keyExtractor={item => item[0]}
            // extraData={navigation}       
          />
        </View>


        <View style={{flexDirection: 'row',}}>
          <View style={{ height: 40, width: '70%'}}>
            <TextInput
              style={{ height: 40, borderWidth: 2, textAlign:'center'}}
              value={this.state.docname}
              onChangeText={docname => this.setState({docname})}
              placeholder='Document name'
            />
          </View>
          <View style={{marginHorizontal: '1%'}} />
          <View style={{ height: 40, width: '30%'}}>
            <Button
              onPress={() => this.addDoc()}
              title="Add"
              color="#841584"
            />
          </View>
        </View>
      </View>
    );

  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    padding: '10%',
  },
});
